// src/pages/SportPage.js
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import SubHeader from '../components/SubHeader';
import NewsCard from '../components/NewsCard';
import ScoreCard from '../components/ScoreCard';
import TweetCard from '../components/TweetCard';

const mockData = {
  baseball: {
    featured: [
      {
        title: 'Yankees Win AL East',
        description: 'Clinched with 5-3 win.',
        image: 'https://via.placeholder.com/300x160?text=Yankees',
      },
    ],
    scores: [
      {
        home: 'NYY',
        away: 'BOS',
        homeScore: '5',
        awayScore: '3',
        status: 'Final',
      },
    ],
    social: [{ text: 'Judge hits 62! #MLB', author: 'YankeeFan' }],
    polls: [],
  },
  // Add others or use same
};

export default function SportPage() {
  const { sportId = 'baseball', tab = 'featured' } = useParams();
  const [data, setData] = useState([]);

  useEffect(() => {
    // Simulate API delay
    setTimeout(() => {
      const sportData = mockData[sportId] || mockData.baseball;
      setData(sportData[tab] || []);
    }, 300);
  }, [sportId, tab]);

  return (
    <>
      <SubHeader />
      <div className="container" style={{ padding: '20px 0' }}>
        <h2 style={{ textTransform: 'capitalize', marginBottom: '16px' }}>
          {sportId} â€” {tab}
        </h2>

        {tab === 'featured' && (
          <div className="card-grid">
            {data.map((item, i) => (
              <NewsCard key={i} {...item} />
            ))}
          </div>
        )}

        {tab === 'scores' && (
          <div style={{ display: 'grid', gap: '12px' }}>
            {data.map((item, i) => (
              <ScoreCard key={i} {...item} />
            ))}
          </div>
        )}

        {tab === 'social' && (
          <div style={{ display: 'grid', gap: '12px' }}>
            {data.map((item, i) => (
              <TweetCard key={i} {...item} />
            ))}
          </div>
        )}

        {tab === 'polls' && (
          <p style={{ textAlign: 'center', color: '#aaa' }}>
            Polls coming soon!
          </p>
        )}
      </div>
    </>
  );
}
