import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import ScoreCard from '../components/ScoreCard';
import axios from 'axios';

const ScoresPage = () => {
  const { sport } = useParams();
  const [scores, setScores] = useState([]);

  useEffect(() => {
    const fetchScores = async () => {
      try {
        const league = sport === 'soccer' ? 'soccer' : `${sport}`;
        const { data } = await axios.get(
          `https://site.api.espn.com/apis/site/v2/sports/${league}/scoreboard`
        );
        setScores(data.events.slice(0, 5));
      } catch (error) {
        console.error('Scores API error:', error);
        setScores([]);
      }
    };
    fetchScores();
  }, [sport]);

  return (
    <SportPageTemplate>
      <div style={{ padding: '20px' }}>
        <h2 style={{ fontSize: '24px', marginBottom: '20px' }}>Scores</h2>
        {scores.map((event, index) => {
          const comp = event.competitions[0];
          const home = comp.competitors.find((c) => c.homeAway === 'home');
          const away = comp.competitors.find((c) => c.homeAway === 'away');
          return (
            <ScoreCard
              key={index}
              home={home.team.abbreviation}
              away={away.team.abbreviation}
              homeScore={home.score || 0}
              awayScore={away.score || 0}
              status={event.status.type.shortDetail}
            />
          );
        })}
      </div>
    </SportPageTemplate>
  );
};

export default ScoresPage;
