// src/pages/HomePage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import NewsCard from '../components/NewsCard';
import ScoreCard from '../components/ScoreCard';

const SPORTS = [
  { id: 'baseball', league: 'mlb' },
  { id: 'basketball', league: 'nba' },
  { id: 'football', league: 'nfl' },
  { id: 'soccer', league: 'eng.1' },
  { id: 'golf', league: 'pga' },
];

export default function HomePage() {
  const [topStories, setTopStories] = useState([]);
  const [upcomingGames, setUpcomingGames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchHomeData = async () => {
      setLoading(true);
      setError(null);

      try {
        // === TOP STORIES (left column) ===
        const storyPromises = SPORTS.map(async (sport) => {
          try {
            const { data } = await axios.get(
              `https://site.api.espn.com/apis/site/v2/sports/${sport.id}/${sport.league}/news`
            );
            return (
              data.articles?.slice(0, 2).map((a) => ({
                title: a.headlines?.[0]?.description || a.title || 'No title',
                description: a.description || 'No description',
                image:
                  a.images?.[0]?.url ||
                  'https://via.placeholder.com/300x160?text=News',
                url: a.links?.web?.href || '#',
                sport: sport.id,
              })) || []
            );
          } catch (err) {
            console.warn(`Failed to fetch ${sport.id} news:`, err);
            return [];
          }
        });

        const storyResults = await Promise.all(storyPromises);
        const allStories = storyResults.flat().slice(0, 5);
        setTopStories(allStories);

        // === UPCOMING GAMES (right column) ===
        const gamePromises = SPORTS.map(async (sport) => {
          try {
            const { data } = await axios.get(
              `https://site.api.espn.com/apis/site/v2/sports/${sport.id}/${sport.league}/scoreboard`
            );
            const events = data.events || [];
            return events.slice(0, 2).map((e) => {
              const comp = e.competitions?.[0];
              const home =
                comp?.competitors?.find((c) => c.homeAway === 'home') || {};
              const away =
                comp?.competitors?.find((c) => c.homeAway === 'away') || {};

              // Golf: Use athlete names
              if (sport.id === 'golf') {
                const leader = comp?.competitors?.[0]?.athlete || {};
                const second = comp?.competitors?.[1]?.athlete || {};
                return {
                  home: leader.displayName || 'Leader',
                  away: second.displayName || 'Runner-Up',
                  homeScore: leader.score || 'TBD',
                  awayScore: second.score || 'TBD',
                  status: e.status?.type?.shortDetail || 'Upcoming',
                  sport: 'golf',
                };
              }

              return {
                home: home.team?.abbreviation || 'HOME',
                away: away.team?.abbreviation || 'AWAY',
                homeScore: home.score || '0',
                awayScore: away.score || '0',
                status: e.status?.type?.shortDetail || 'Upcoming',
                sport: sport.id,
              };
            });
          } catch (err) {
            console.warn(`Failed to fetch ${sport.id} games:`, err);
            return [];
          }
        });

        const gameResults = await Promise.all(gamePromises);
        const allGames = gameResults.flat().slice(0, 5);
        setUpcomingGames(allGames);
      } catch (err) {
        console.error('Home page API error:', err);
        setError('Failed to load data. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchHomeData();
  }, []);

  return (
    <div
      className="container"
      style={{ padding: '100px 20px 40px', minHeight: 'calc(100vh - 180px)' }}
    >
      <h1
        style={{
          fontSize: '32px',
          textAlign: 'center',
          marginBottom: '32px',
          color: '#ff6b35',
        }}
      >
        OVERTIME — Home
      </h1>

      {loading ? (
        <p style={{ textAlign: 'center', color: '#aaa', fontSize: '18px' }}>
          Loading latest updates...
        </p>
      ) : error ? (
        <p style={{ textAlign: 'center', color: '#ff6b35', fontSize: '18px' }}>
          {error}
        </p>
      ) : (
        <div className="home-grid">
          {/* LEFT: Top Stories */}
          <section className="home-stories">
            <h2
              style={{ fontSize: '24px', marginBottom: '16px', color: '#fff' }}
            >
              Top Stories
            </h2>
            <div className="card-grid">
              {topStories.map((story, i) => (
                <NewsCard key={i} {...story} />
              ))}
            </div>
          </section>

          {/* RIGHT: Upcoming Games */}
          <section className="home-games">
            <h2
              style={{ fontSize: '24px', marginBottom: '16px', color: '#fff' }}
            >
              Upcoming Games
            </h2>
            <div className="upcoming-games-list">
              {upcomingGames.length === 0 ? (
                <p style={{ color: '#aaa', fontStyle: 'italic' }}>
                  No upcoming games right now.
                </p>
              ) : (
                upcomingGames.map((game, i) => <ScoreCard key={i} {...game} />)
              )}
            </div>
          </section>
        </div>
      )}
    </div>
  );
}
