// src/pages/SportPage.js
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
// import SubHeader from '../components/SubHeader';
import NewsCard from '../components/NewsCard';
import ScoreCard from '../components/ScoreCard';
import TweetCard from '../components/TweetCard';
import axios from 'axios';

/* -------------------------------------------------
   MOCK DATA – keep your existing featured/scores
   ------------------------------------------------- */
const mockData = {
  baseball: {
    featured: [
      {
        title: 'Judge Hits 62!',
        description: 'Breaks AL record.',
        image: 'https://via.placeholder.com/300x160?text=Judge+62',
      },
      {
        title: 'Yankees Clinch',
        description: 'AL East champs.',
        image: 'https://via.placeholder.com/300x160?text=YANKEES',
      },
    ],
    scores: [
      {
        home: 'NYY',
        away: 'BOS',
        homeScore: '5',
        awayScore: '3',
        status: 'Final',
      },
      {
        home: 'LAD',
        away: 'SFG',
        homeScore: '4',
        awayScore: '2',
        status: '7th',
      },
    ],
    social: [], // filled by API
    polls: [],
  },
  // Add other sports if you like – fallback to baseball
};

/* -------------------------------------------------
   COMPONENT
   ------------------------------------------------- */
export default function SportPage() {
  const { sportId = 'baseball', tab = 'featured' } = useParams();
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  /* ----- SOCIAL TAB – X (Twitter) API ----- */
  useEffect(() => {
    if (tab !== 'social') return;

    const fetchTweets = async () => {
      setLoading(true);
      const BEARER = process.env.REACT_APP_TWITTER_BEARER_TOKEN;
      const query = `"${sportId}" -is:retweet lang:en`;

      try {
        if (BEARER) {
          const { data } = await axios.get(
            'https://api.twitter.com/2/tweets/search/recent',
            {
              headers: { Authorization: `Bearer ${BEARER}` },
              params: {
                query,
                max_results: 10,
                // NOTE: dot in key → use bracket notation
                'tweet.fields': 'author_id,public_metrics',
              },
            }
          );

          const tweets = (data.data || []).map((t) => ({
            id: t.id,
            text: t.text,
            author: t.author_id,
            likes: t.public_metrics?.like_count ?? 0,
            retweets: t.public_metrics?.retweet_count ?? 0,
          }));
          setData(tweets);
        } else {
          // No token → mock data
          setData(mockTweets(sportId));
        }
      } catch (err) {
        console.error('Twitter API error:', err);
        setData(mockTweets(sportId));
      } finally {
        setLoading(false);
      }
    };

    fetchTweets();
  }, [sportId, tab]);

  /* ----- OTHER TABS – use mock data ----- */
  useEffect(() => {
    if (tab === 'social') return;

    setTimeout(() => {
      const sport = mockData[sportId] || mockData.baseball;
      setData(sport[tab] || []);
      setLoading(false);
    }, 300);
  }, [sportId, tab]);

  /* ----- MOCK TWEETS (fallback) ----- */
  const mockTweets = (sport) => [
    {
      id: 'm1',
      text: `${sport} fans are hyped! #${sport.toUpperCase()}`,
      author: 'Fan1',
      likes: 150,
      retweets: 25,
    },
    {
      id: 'm2',
      text: `Insane finish in ${sport} today!`,
      author: 'Fan2',
      likes: 120,
      retweets: 20,
    },
    {
      id: 'm3',
      text: `Who wins ${sport} MVP?`,
      author: 'Expert3',
      likes: 95,
      retweets: 15,
    },
    {
      id: 'm4',
      text: `Highlight of the week in ${sport}!`,
      author: 'Highlight4',
      likes: 80,
      retweets: 12,
    },
    {
      id: 'm5',
      text: `Underrated ${sport} player right now.`,
      author: 'Underrated5',
      likes: 70,
      retweets: 10,
    },
    {
      id: 'm6',
      text: `${sport} memes are gold`,
      author: 'Meme6',
      likes: 65,
      retweets: 9,
    },
    {
      id: 'm7',
      text: `Trade rumors in ${sport}.`,
      author: 'Rumors7',
      likes: 60,
      retweets: 8,
    },
    {
      id: 'm8',
      text: `Best ${sport} moment this season.`,
      author: 'Moment8',
      likes: 55,
      retweets: 7,
    },
    {
      id: 'm9',
      text: `Shoutout to ${sport} rookies!`,
      author: 'Rookie9',
      likes: 50,
      retweets: 6,
    },
    {
      id: 'm10',
      text: `Hot take on ${sport}?`,
      author: 'HotTake10',
      likes: 45,
      retweets: 5,
    },
  ];

  /* ----- RENDER ----- */
  return (
    <>
      {/* <SubHeader /> */}
      <div
        className="container"
        style={{ padding: '20px 0', minHeight: 'calc(100vh - 180px)' }}
      >
        <h2 style={{ textTransform: 'capitalize', marginBottom: '16px' }}>
          {sportId} — {tab}
        </h2>

        {loading ? (
          <p style={{ textAlign: 'center', color: '#aaa' }}>Loading…</p>
        ) : (
          <>
            {/* FEATURED */}
            {tab === 'featured' && (
              <div className="card-grid">
                {data.map((item, i) => (
                  <NewsCard key={i} {...item} />
                ))}
              </div>
            )}

            {/* SCORES */}
            {tab === 'scores' && (
              <div style={{ display: 'grid', gap: '12px' }}>
                {data.map((item, i) => (
                  <ScoreCard key={i} {...item} />
                ))}
              </div>
            )}

            {/* SOCIAL */}
            {tab === 'social' && (
              <div style={{ display: 'grid', gap: '12px' }}>
                {data.map((t) => (
                  <TweetCard
                    key={t.id}
                    text={t.text}
                    author={t.author}
                    likes={t.likes}
                    retweets={t.retweets}
                  />
                ))}
              </div>
            )}

            {/* POLLS */}
            {tab === 'polls' && (
              <p style={{ textAlign: 'center', color: '#aaa' }}>
                Polls coming soon!
              </p>
            )}
          </>
        )}
      </div>
    </>
  );
}
