import React from 'react';

const NewsCard = ({ title, description, image }) => {
  return (
    <div className="card news-card">
      <img
        src={image || 'https://via.placeholder.com/300x150?text=News'}
        alt={title}
      />
      <h3 style={{ fontSize: '18px', margin: '8px 0' }}>{title}</h3>
      <p style={{ color: '#aaa', fontSize: '14px' }}>{description}</p>
    </div>
  );
};

export default NewsCard;
