// src/pages/HomePage.js
import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function HomePage() {
  const navigate = useNavigate();

  useEffect(() => {
    // Optional: Auto-redirect Home → Baseball Featured
    // Remove if you want a real home screen
    // navigate('/baseball/featured', { replace: true });
  }, [navigate]);

  return (
    <div
      className="container"
      style={{
        padding: '20px 0',
        paddingTop: '80px',
        minHeight: 'calc(100vh - 180px)',
        textAlign: 'center',
      }}
    >
      <h1 style={{ fontSize: '32px', marginBottom: '20px', color: '#ff6b35' }}>
        OVERTIME
      </h1>
      <p style={{ color: '#aaa', fontSize: '18px' }}>
        Select a tab above or a sport below.
      </p>
    </div>
  );
}
