        status: '3rd',
      },
      {
        home: 'Collin Morikawa',
        away: 'PGA Tour',
        homeScore: '1,600',
        awayScore: '4th',
        status: '4th',
      },
      {
        home: 'Viktor Hovland',
        away: 'PGA Tour',
        homeScore: '1,400',
        awayScore: '5th',
        status: '5th',
      },
    ],
  },
};

export default function StandingsPage() {
  return (
    <div className="container" style={{ padding: '100px 20px 40px' }}>
      <h1
        style={{ fontSize: '32px', textAlign: 'center', marginBottom: '32px' }}
      >
        Standings
      </h1>
      <div className="standings-grid">
        {Object.entries(STANDINGS_DATA).map(([sport, data]) => (
          <section key={sport}>
            <h2
              style={{ fontSize: '24px', marginBottom: '16px', color: '#fff' }}
            >
              {data.title}
            </h2>
            <div className="standings-list">
              {data.entries.map((entry, i) => (
                <ScoreCard key={i} {...entry} />
              ))}
            </div>
          </section>
        ))}
      </div>
    </div>
  );
}
