import React from 'react';

const ScoreCard = ({ home, away, homeScore, awayScore, status }) => {
  return (
    <div className="card score-card">
      <div>
        <p>{home}</p>
        <p style={{ fontSize: '24px', fontWeight: 'bold' }}>{homeScore}</p>
      </div>
      <span>vs</span>
      <div>
        <p>{away}</p>
        <p style={{ fontSize: '24px', fontWeight: 'bold' }}>{awayScore}</p>
      </div>
      <p style={{ color: '#aaa' }}>{status}</p>
    </div>
  );
};

export default ScoreCard;
