import React from 'react';
import { Link, useParams } from 'react-router-dom';

const SubHeader = () => {
  const { sport } = useParams();
  const tabs = ['Featured', 'Scores', 'Social', 'Polls'];
  const currentTab = useParams().tab || 'featured';

  return (
    <nav className="sticky-subheader">
      <div className="subheader-nav">
        {tabs.map((tab) => (
          <Link
            key={tab}
            to={`/${sport}/${tab.toLowerCase()}`}
            className={`subheader-link ${
              currentTab === tab.toLowerCase() ? 'active' : ''
            }`}
          >
            {tab}
          </Link>
        ))}
      </div>
    </nav>
  );
};

export default SubHeader;
