import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import NewsCard from '../components/NewsCard';
import axios from 'axios';

const FeaturedPage = () => {
  const { sport } = useParams();
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    // FOX Sports-like data via ESPN API (no key needed)
    const fetchArticles = async () => {
      try {
        const league = sport === 'soccer' ? 'soccer' : `${sport}`;
        const { data } = await axios.get(
          `https://site.api.espn.com/apis/site/v2/sports/${league}/news`
        );
        setArticles(data.articles.slice(0, 5));
      } catch (error) {
        console.error('Featured API error:', error);
        setArticles([]); // Fallback
      }
    };
    fetchArticles();
  }, [sport]);

  return (
    <SportPageTemplate>
      <div style={{ padding: '20px' }}>
        <h2 style={{ fontSize: '24px', marginBottom: '20px' }}>
          Featured Stories
        </h2>
        {articles.map((article, index) => (
          <NewsCard
            key={index}
            title={article.headline || 'No title'}
            description={article.description || 'No description'}
            image={
              article.images && article.images[0] ? article.images[0].url : null
            }
          />
        ))}
      </div>
    </SportPageTemplate>
  );
};

export default FeaturedPage;
