// src/App.js
import React from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useLocation,
} from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import SubHeader from './components/SubHeader';
import HomePage from './pages/HomePage';
import SportPage from './pages/SportPage';
import './index.css';

function Layout() {
  const location = useLocation();
  const isHome = location.pathname === '/';
  const isSportPage = location.pathname.match(/^\/[a-z]+(\/|$)/i); // /baseball, /basketball, etc.

  return (
    <div className="app-wrapper">
      <Header />
      {/* Only show SubHeader on sport pages */}
      {isSportPage && !isHome && <SubHeader />}
      <main className="main-content">
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/:sportId" element={<SportPage />} />
          <Route path="/:sportId/:tab" element={<SportPage />} />
        </Routes>
      </main>
      <Footer />
    </div>
  );
}

export default function App() {
  return (
    <Router>
      <Layout />
    </Router>
  );
}
