import React from 'react';
import { Link } from 'react-router-dom';
import {
  FaBaseballBall,
  FaBasketballBall,
  FaFutbol,
  FaGolfBall,
  FaFootballBall,
} from 'react-icons/fa';

const sports = [
  { id: 'baseball', icon: <FaBaseballBall />, name: 'Baseball' },
  { id: 'basketball', icon: <FaBasketballBall />, name: 'Basketball' },
  { id: 'football', icon: <FaFootballBall />, name: 'Football' },
  { id: 'soccer', icon: <FaFutbol />, name: 'Soccer' },
  { id: 'golf', icon: <FaGolfBall />, name: 'Golf' },
];

const Footer = () => {
  return (
    <footer className="sticky-footer">
      <nav className="footer-nav">
        {sports.map((sport) => (
          <Link
            key={sport.id}
            to={`/${sport.id}`}
            className="footer-link"
            style={{ flex: '1' }}
          >
            <div className="footer-icon">{sport.icon}</div>
            <span style={{ fontSize: '12px' }}>{sport.name}</span>
          </Link>
        ))}
      </nav>
    </footer>
  );
};

export default Footer;
