// src/pages/HomePage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import NewsCard from '../components/NewsCard';
import ScoreCard from '../components/ScoreCard';

const SPORTS = [
  { id: 'baseball', league: 'mlb' },
  { id: 'basketball', league: 'nba' },
  { id: 'football', league: 'nfl' },
  { id: 'soccer', league: 'soccer' },
  { id: 'golf', league: 'golf' },
];

export default function HomePage() {
  const [topStories, setTopStories] = useState([]);
  const [liveScores, setLiveScores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchHomeData = async () => {
      setLoading(true);
      setError(null);

      try {
        // Fetch top stories from all sports
        const storyPromises = SPORTS.map(async (sport) => {
          try {
            const { data } = await axios.get(
              `https://site.api.espn.com/apis/site/v2/sports/${sport.id}/${sport.league}/news`
            );
            return data.articles.slice(0, 2).map((a) => ({
              title: a.headlines?.[0]?.description || a.title || 'No title',
              description: a.description || 'No description',
              image:
                a.images?.[0]?.url ||
                'https://via.placeholder.com/300x160?text=News',
              url: a.links?.web?.href || '#',
              sport: sport.id,
            }));
          } catch (err) {
            console.warn(`Failed to fetch ${sport.id} news:`, err);
            return [];
          }
        });

        const storyResults = await Promise.all(storyPromises);
        const allStories = storyResults.flat().slice(0, 5);
        setTopStories(allStories);

        // Fetch live scores
        const scorePromises = SPORTS.map(async (sport) => {
          try {
            const { data } = await axios.get(
              `https://site.api.espn.com/apis/site/v2/sports/${sport.id}/${sport.league}/scoreboard`
            );
            return (data.events || []).slice(0, 2).map((e) => {
              const comp = e.competitions[0];
              const home =
                comp.competitors.find((c) => c.homeAway === 'home') || {};
              const away =
                comp.competitors.find((c) => c.homeAway === 'away') || {};
              return {
                home: home.team?.abbreviation || 'HOME',
                away: away.team?.abbreviation || 'AWAY',
                homeScore: home.score || '0',
                awayScore: away.score || '0',
                status: e.status?.type?.shortDetail || 'Upcoming',
                sport: sport.id,
              };
            });
          } catch (err) {
            console.warn(`Failed to fetch ${sport.id} scores:`, err);
            return [];
          }
        });

        const scoreResults = await Promise.all(scorePromises);
        const allScores = scoreResults.flat().slice(0, 5);
        setLiveScores(allScores);
      } catch (err) {
        console.error('Home page API error:', err);
        setError('Failed to load data. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchHomeData();
  }, []);

  return (
    <div
      className="container"
      style={{ padding: '100px 20px 40px', minHeight: 'calc(100vh - 180px)' }}
    >
      <h1
        style={{
          fontSize: '32px',
          textAlign: 'center',
          marginBottom: '32px',
          color: '#ff6b35',
        }}
      >
        OVERTIME — Top Stories & Live Scores
      </h1>

      {loading ? (
        <p style={{ textAlign: 'center', color: '#aaa', fontSize: '18px' }}>
          Loading latest updates...
        </p>
      ) : error ? (
        <p style={{ textAlign: 'center', color: '#ff6b35', fontSize: '18px' }}>
          {error}
        </p>
      ) : (
        <>
          {/* Top Stories */}
          <section style={{ marginBottom: '48px' }}>
            <h2
              style={{ fontSize: '24px', marginBottom: '16px', color: '#fff' }}
            >
              Top Stories
            </h2>
            <div className="card-grid">
              {topStories.map((story, i) => (
                <NewsCard
                  key={i}
                  title={story.title}
                  description={story.description}
                  image={story.image}
                  url={story.url}
                />
              ))}
            </div>
          </section>

          {/* Live Scores */}
          <section>
            <h2
              style={{ fontSize: '24px', marginBottom: '16px', color: '#fff' }}
            >
              Live & Upcoming Games
            </h2>
            <div style={{ display: 'grid', gap: '16px' }}>
              {liveScores.map((score, i) => (
                <ScoreCard
                  key={i}
                  home={score.home}
                  away={score.away}
                  homeScore={score.homeScore}
                  awayScore={score.awayScore}
                  status={score.status}
                />
              ))}
            </div>
          </section>
        </>
      )}
    </div>
  );
}
