import { NavLink, useParams } from 'react-router-dom';
import StadiumIcon from './StadiumIcon';
import './Header.css';

export default function Header() {
  const { sportId = 'baseball' } = useParams();
  const isHome = !sportId || sportId === '/';

  return (
    <header className="app-header">
      <div className="header-container">
        {/* Logo with Stadium Icon */}
        <NavLink to="/" className="header-logo">
          <StadiumIcon size={24} />
          <span>OVERTIME</span>
        </NavLink>

        {/* Text Links â€“ Only on sport pages */}
        {!isHome && (
          <nav className="header-nav">
            <NavLink
              to={`/${sportId}/scores`}
              className={({ isActive }) =>
                `header-link ${isActive ? 'active' : ''}`
              }
              end
            >
              Scores
            </NavLink>
            <NavLink
              to={`/${sportId}/social`}
              className={({ isActive }) =>
                `header-link ${isActive ? 'active' : ''}`
              }
              end
            >
              Social
            </NavLink>
            <NavLink
              to={`/${sportId}/polls`}
              className={({ isActive }) =>
                `header-link ${isActive ? 'active' : ''}`
              }
              end
            >
              Polls
            </NavLink>
            <NavLink
              to={`/${sportId}/standings`}
              className={({ isActive }) =>
                `header-link ${isActive ? 'active' : ''}`
              }
              end
            >
              Standings
            </NavLink>
          </nav>
        )}
      </div>
    </header>
  );
}
