// src/components/PollCard.js
import React, { useState, useEffect } from 'react';

const PollCard = ({ polls }) => {
  const [votes, setVotes] = useState({}); // { pollId: { optionId: count } }
  const [selectedPoll, setSelectedPoll] = useState(null); // For popup
  const [selectedOption, setSelectedOption] = useState(null); // Option in popup
  const [showThanks, setShowThanks] = useState(null); // pollId for thank you

  // Initialize votes from localStorage
  useEffect(() => {
    const initialVotes = {};
    polls.forEach((poll) => {
      const saved = localStorage.getItem(`poll-${poll.id}`);
      initialVotes[poll.id] = saved
        ? JSON.parse(saved)
        : poll.options.reduce((acc, opt) => ({ ...acc, [opt.id]: 0 }), {});
    });
    setVotes(initialVotes);
  }, [polls]);

  // Save votes to localStorage
  useEffect(() => {
    Object.keys(votes).forEach((pollId) => {
      localStorage.setItem(`poll-${pollId}`, JSON.stringify(votes[pollId]));
    });
  }, [votes]);

  const handleOpenVotePopup = (pollId, optionId) => {
    setSelectedPoll(pollId);
    setSelectedOption(optionId);
  };

  const handleVote = (pollId, optionId) => {
    const pollVotes = votes[pollId];
    if (!pollVotes) return;

    // Check if already voted
    const hasVoted = Object.values(pollVotes).some((count) => count > 0);
    if (hasVoted) return;

    // Record vote
    setVotes((prev) => ({
      ...prev,
      [pollId]: {
        ...prev[pollId],
        [optionId]: prev[pollId][optionId] + 1,
      },
    }));

    // Show thank you and close popup
    setShowThanks(pollId);
    setTimeout(() => {
      setShowThanks(null);
      setSelectedPoll(null);
      setSelectedOption(null);
    }, 2000);
  };

  return (
    <div className="poll-card">
      <h3 className="poll-title">Fan Polls</h3>
      <div className="polls-list">
        {polls.map((poll) => {
          const pollVotes = votes[poll.id] || {};
          const totalVotes = Object.values(pollVotes).reduce(
            (a, b) => a + b,
            0
          );
          const safeTotal = totalVotes > 0 ? totalVotes : 1;
          const userVotedOption = Object.keys(pollVotes).find(
            (id) => pollVotes[id] > 0
          );

          return (
            <div key={poll.id} className="poll-item">
              <h4 className="poll-question">{poll.question}</h4>
              <div className="poll-options">
                {poll.options.map((opt) => {
                  const percentage = Math.round(
                    ((pollVotes[opt.id] || 0) / safeTotal) * 100
                  );
                  const isUserVote = userVotedOption === opt.id;

                  return (
                    <div
                      key={opt.id}
                      className={`poll-option ${
                        userVotedOption ? 'voted' : 'clickable'
                      }`}
                      onClick={() =>
                        !userVotedOption && handleOpenVotePopup(poll.id, opt.id)
                      }
                    >
                      <div className="poll-label">
                        <span>{opt.text}</span>
                        <span className="poll-pct">{percentage}%</span>
                      </div>
                      <div className="poll-bar-bg">
                        <div
                          className="poll-bar"
                          style={{ width: `${percentage}%` }}
                        />
                      </div>
                      {isUserVote && (
                        <span className="your-vote">Your vote</span>
                      )}
                    </div>
                  );
                })}
              </div>
              <div className="poll-total">
                {totalVotes} vote{totalVotes !== 1 ? 's' : ''}
              </div>

              {/* Vote Popup */}
              {selectedPoll === poll.id && selectedOption && (
                <div className="vote-popup">
                  <div className="vote-popup-content">
                    {showThanks === poll.id ? (
                      <p className="vote-thank-you">Thank you for your vote!</p>
                    ) : (
                      <>
                        <p className="vote-prompt">
                          Vote for:{' '}
                          {
                            poll.options.find(
                              (opt) => opt.id === selectedOption
                            )?.text
                          }
                          ?
                        </p>
                        <button
                          className="vote-confirm-btn"
                          onClick={() => handleVote(poll.id, selectedOption)}
                        >
                          Vote
                        </button>
                        <button
                          className="vote-cancel-btn"
                          onClick={() => {
                            setSelectedPoll(null);
                            setSelectedOption(null);
                          }}
                        >
                          Cancel
                        </button>
                      </>
                    )}
                  </div>
                </div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default PollCard;
