// src/components/Header.js
import { NavLink, useParams } from 'react-router-dom';
import { FiRss, FiTrendingUp, FiTwitter, FiBarChart3 } from 'react-icons/fi';
import StadiumIcon from './StadiumIcon';
import './Header.css';

const tabs = [
  { id: 'featured', label: 'Featured', icon: FiRss },
  { id: 'scores', label: 'Scores', icon: FiTrendingUp },
  { id: 'social', label: 'Social', icon: FiTwitter },
  { id: 'polls', label: 'Polls', icon: FiBarChart3 },
];

export default function Header() {
  const { sportId = 'baseball' } = useParams();
  const isHome = !sportId || sportId === '/';

  return (
    <header className="app-header">
      <div className="header-container">
        {/* Logo with Stadium Icon */}
        <NavLink to="/" className="header-logo">
          <StadiumIcon size={24} />
          <span>OVERTIME</span>
        </NavLink>

        {/* Tab Icons â€“ Only on sport pages */}
        {!isHome && (
          <nav className="header-tabs">
            {tabs.map(({ id, label, icon: Icon }) => (
              <NavLink
                key={id}
                to={`/${sportId}/${id}`}
                className={({ isActive }) =>
                  `header-tab ${isActive ? 'active' : ''}`
                }
                title={label}
                end
              >
                <Icon size={20} />
              </NavLink>
            ))}
          </nav>
        )}
      </div>
    </header>
  );
}
